columns.script:
let rowText = data.row['Enabled'] ? 'Enabled' : 'Disabled'
let rowColor = data.row['Enabled'] ? 'btn badge btn-success opacity-75' : 'btn badge btn-danger opacity-75'

return {
 'type': 'button',
   'text': rowText,
   'class': rowColor,
 'style': {
 	'text-decoration':'underline',
   'font-weight': 'bold'
 }

}

let rowText = ''
let rowColor = 'btn badge btn-dark opacity-85'

return {
 'type': 'button',
   'text': rowText,
   'class': rowColor,
  'icon': 'user-group',
 'style': {
 	'text-decoration':'underline',
   'font-weight': 'bold'
 }

}

let status = true;

try { status = data.row['AuthMethod'].length < 0 } catch(e) {}

if(status) {
  let rowText = 'Reset Password'
  let rowColor = 'btn badge btn-dark opacity-85'

  return {
   'type': 'button',
     'text': rowText,
     'class': rowColor,
    'icon': 'check',
   'style': {
      'text-decoration':'underline',
     'font-weight': 'bold' }

  }
} else {
  return {
   'type': 'text',
     'text': '',
     'class': '',
    'icon': '',
   'style': {
      'text-decoration':'underline',
     'font-weight': 'bold' }

  }
}

let rowText = 'Reset MFA'
let rowColor = 'btn badge btn-warning opacity-85'

return {
 'type': 'button',
   'text': rowText,
   'class': rowColor,
  'icon': 'check',
 'style': {
 	'text-decoration':'underline',
   'font-weight': 'bold', 'color': 'black'
 }

}

let rowText = ''
let rowColor = 'btn badge btn-danger opacity-85'

return {
 'type': 'button',
   'text': rowText,
   'class': rowColor,
  'icon': 'eye-slash',
 'style': {
 	'text-decoration':'underline',
   'font-weight': 'bold'
 }

}


